import ioimport webbrowserfrom tkinter import *import requestsfrom urllib.request import urlopenfrom PIL import ImageTk, Imageclass Newsapp:    def __init__(self):        self.data=requests.get('https://newsapi.org/v2/top-headlines?country=us&apiKey=f8bc21d568ba450e94b0a6bb37d82c68').json()        self.open_gui()        self.get_news_data(0)    def open_gui(self):        self.root = Tk()        self.root.title('News App')        self.root.geometry('350x600')        self.root.resizable(0,0)        self.root.config(background='black')    def clear(self):        for i in self.root.pack_slaves():            i.destroy()    def get_news_data(self, index):        self.clear()        try:            image_url = self.data['articles'][index]['urlToImage']            raw_data = urlopen(image_url).read()            im = Image.open(io.BytesIO(raw_data)).resize((350,250))            photo = ImageTk.PhotoImage(im)        except:            image_url = 'https://diverseps.com/product/loadlink-addin-for-deckman/'            raw_data = urlopen(image_url).read()            im = Image.open(io.BytesIO(raw_data)).resize((350, 250))            photo = ImageTk.PhotoImage(im)        header = Label(self.root, text='Trending News Today', wraplength=350, bg='black', fg='white',                       font = ('arial', 20, 'bold'))        header.pack(pady=(20,10))        image_lbl = Label(self.root, image=photo)        image_lbl.pack(pady=(20,10))        title = Label(self.root, text=self.data['articles'][index]['title'], bg='black', fg='white', wraplength=350,                      font = ('arial', 18))        title.pack()        descript = Label(self.root, text=self.data['articles'][index]['description'], wraplength=350, bg='black', fg='white',                         font=('arial', 16))        descript.pack(pady=(10,20))        frame = Frame(self.root, background='black')        frame.pack(expand=True, fill=BOTH)        if index !=0:            prev = Button(frame, text = "Prev", width=9, height=3, command=lambda :self.get_news_data(index-1))            prev.pack(side=LEFT)        read_more = Button(frame, text="Read More", width=9, height=3, command=lambda :self.open_link(self.data['articles'][index]['url']))        read_more.pack(side=LEFT)        if index != len(self.data['articles'])-1:            next=Button(frame, text="Next", width=9, height=3, command=lambda :self.get_news_data(index+1))            next.pack(side=LEFT)        self.root.mainloop()    def open_link(self, url):            webbrowser.open(url)obj=Newsapp()